package es.Studium.ejercicio4Tema3.Inicializar;


public class Inicializar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int c=3;
		int i;
		int tabla[] = new int[c];
		for(i=0;i<c;i++)
		{
		tabla[i] = 0;
		}
		for(i=0;i<c;i++)
		{
			System.out.print("Tabla["+i+"]="+tabla[i]+" ");
		}
		// Recorrido para mostrar los valores de la tabla 
		
	}
}
